import supabase from "../config/supabaseClient";
import { useEffect, useState } from "react";

import SmoothieCard from "../components/SmoothieCard";

const Home = () => {
  const [fetchError, setFetchError] = useState(null);
  const [smoothies, setSmoothies] = useState(null);

useEffect(()=>{
  const fetchSmoothies = async () =>{
    const {data: Smoothies, error} = await supabase
    .from('Smoothies') //name of table of supabase
    .select() //grabs all items in specified table when blank

    if(error) {
      setFetchError('Could not fetch the smoothies');
      setSmoothies(null)
      console.log(error)
    }
    if(smoothies){
      setSmoothies(smoothies);
      setFetchError(null)
    }
  }
  fetchSmoothies() //function call
}, [])

  return (
    <div className="page home">
    {fetchError && (<p>{fetchError}</p>)}
    {smoothies && (<div className="smoothies">
    {/* order using buttons */}
    <div className="smoothie-grid">
      {smoothies.map(smoothie => (
        <p>{smoothie.title}</p>
      ))}
    </div>
        </div>
        )}
        </div>
  )
};

export default Home
